mapper.select:
SELECT D.DOG_NO, D.BREED_NO, DB.BREED_NAME, D.DOG_NAME,
NVL( CASE WHEN MONTHS_BETWEEN(CURRENT_DATE, D.DOG_BIRTHDAY) <= 12 THEN ROUND(MONTHS_BETWEEN(CURRENT_DATE, D.DOG_BIRTHDAY)) || '개월' ELSE ROUND(MONTHS_BETWEEN(CURRENT_DATE, D.DOG_BIRTHDAY) / 12) || '세' END, '알 수 없음' ) DOG_BIRTHDAY,
D.DOG_GENDER, D.DOG_SIZE, D.DOG_PERSONALITY, CASE WHEN D.DOG_NEUTERING_YN = 'Y' THEN '완료' ELSE '미완료' END AS DOG_NEUTERING_YN, CASE WHEN D.DOG_VACCINE_YN = 'Y' THEN '완료' ELSE '미완료' END AS DOG_VACCINE_YN, D.DOG_LIKE, D.DOG_HATE, CASE WHEN D.DOG_SIGNIFICANT IS NULL THEN '-' ELSE DOG_SIGNIFICANT END AS DOG_SIGNIFICANT, TO_CHAR(D.DOG_ADMISSION_DATE, 'YYYY"년" MM"월" DD"일"') DOG_ADMISSION_DATE FROM "DOG" D JOIN DOG_BREED DB ON D.BREED_NO = DB.BREED_NO WHERE D.DOG_ADOPT_FL = 'N' AND D.DOG_NO = #{dogNo}

SELECT * FROM DOG_IMG
WHERE DOG_NO = #{dogNo}
ORDER BY IMG_ORDER

SELECT COUNT(*) FROM DOG_LIKE
WHERE DOG_NO = #{dogNo}
AND MEMBER_NO = #{memberNo}

SELECT COUNT(*) FROM DOG_RESERVATION
WHERE MEMBER_NO = #{memberNo}
AND DOG_NO = #{dogNo}
AND RESERVE_YN = 'Y'

mapper.delete:
DELETE FROM "DOG_LIKE"
WHERE DOG_NO = #{dogNo}
AND MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO DOG_IMG SELECT SEQ_DOG_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.dogNo} DOG_NO, #{img.imagePath} IMG_PATH, #{img.imageReName} IMG_RENAME, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageOrder} IMG_ORDER FROM DUAL
) A

mapper.delete:
DELETE FROM "DOG_IMG"
WHERE DOG_NO = #{dogNo}
AND IMG_ORDER IN( ${deleteList} )

mapper.update:
UPDATE "DOG_IMG" SET
IMG_PATH = #{imagePath},
IMG_ORIGINAL = #{imageOriginal},
IMG_RENAME = #{imageReName}
WHERE DOG_NO = #{dogNo}
AND IMG_ORDER = #{imageOrder}

mapper.insert:
INSERT INTO ALARM SELECT SEQ_ALARM_NO.NEXTVAL, A.* FROM (
SELECT #{memberNo} MEMBER_NO, 'N' CHECK_YN, 'D' ALARM_CATEGORY, (SELECT IMG_PATH FROM DOG_IMG WHERE DOG_NO = #{dogNo} AND IMG_ORDER = 0) ALARM_IMG_PATH, (SELECT IMG_RENAME FROM DOG_IMG WHERE DOG_NO = #{dogNo} AND IMG_ORDER = 0) ALARM_IMG_RENAME FROM DUAL
) A
