mapper.insert:
INSERT INTO "REVIEW_IMG" SELECT SEQ_REVIEW_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.imageNo} REVIEW_NO, #{img.imagePath} IMG_PATH, #{img.imageReName} IMG_RENAME, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageOrder} IMG_ORDER FROM DUAL
) A

mapper.delete:
DELETE FROM "REVIEW_IMG"
WHERE REVIEW_NO = #{reviewNo}
AND IMG_ORDER IN ( ${deleteList} )

mapper.update:
UPDATE "REVIEW_IMG" SET
IMG_PATH = #{imagePath},
IMG_ORIGINAL = #{imageOriginal},
IMG_RENAME = #{imageReName}
WHERE REVIEW_NO = #{reviewNo}
AND IMG_ORDER = #{imageOrder}

UPDATE REVIEW SET 
REVIEW_YN = 'Y'
WHERE REVIEW_NO = #{reviewNo}

mapper.select:
  		SELECT * FROM REVIEW_IMG
		WHERE REVIEW_NO = #{reviewNo}
		ORDER BY IMG_ORDER

mapper.update:
UPDATE "REVIEW" SET
REVIEW_VIEWS_COUNT = REVIEW_VIEWS_COUNT + 1
WHERE REVIEW_NO = #{reviewNo}

UPDATE REVIEW_COMMENT SET
COMMENT_CONTENT = #{commentContent}, COMMENT_DATE = SYSDATE
WHERE COMMENT_NO = #{commentNo}
